svg.script:
const regions = document.querySelectorAll('path[id^="region-"]');
const regionName = document.getElementById('region-name');

regions.forEach(region => {
  region.addEventListener('mouseover', () => {
    const regionId = region.id.split('-')[1];
    regionName.textContent = getRegionName(regionId); // Replace with function to get region name based on ID
    regionName.setAttribute('opacity', 1);
  });

  region.addEventListener('mouseout', () => {
    regionName.setAttribute('opacity', 0);
  });
});

function getRegionName(regionId) {
  // Replace this with logic to return the actual region name based on ID
  switch (regionId) {
    case '1':
      return "Fergana";
    case '2':
      return "Andijon";
    // ... (similar cases for other regions)
    default:
      return "Unknown Region";
  }
}
